head.script:
//
(function() {
  'use strict';

  window.Prism = window.Prism || {};
  Prism.manual = false;

  function highlightCode() {
    if (typeof Prism !== 'undefined' && Prism.highlightAll) {
      var codeBlocks = document.querySelectorAll('pre code, code[class*="language-"]');

      codeBlocks.forEach(function(block) {
        if (block.parentElement && block.parentElement.tagName === 'PRE') {
          block.parentElement.classList.add('line-numbers');
        }

        if (!block.className || block.className === '') {
          block.className = 'language-markup';
        }
      });

      Prism.highlightAll();
    }
  }

  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', highlightCode);
  } else {
    highlightCode();
  }

  window.addEventListener('load', highlightCode);
})();
//

