document.addEventListener('DOMContentLoaded', function() {
  // Add magnetic effect to social cards
  const socialCards = document.querySelectorAll('.social-bento-card');

  socialCards.forEach(card => {
    card.addEventListener('mouseenter', function() {
      this.style.borderColor = getComputedStyle(this).getPropertyValue('--hover-color') || 'rgba(59, 130, 246, 0.6)';
    });

    card.addEventListener('mouseleave', function() {
      this.style.borderColor = '';
    });
  });

  // Track social link clicks (optional analytics)
  socialCards.forEach(card => {
    card.addEventListener('click', function() {
      const platform = this.getAttribute('data-platform');
      console.log('Social link clicked:', platform);
      // Add your analytics tracking here if needed
    });
  });
});
