body.script:
//
(function() {
  'use strict';

  // Configuration
  const WORDS_PER_MINUTE = 200; // Average reading speed
  const MIN_READ_TIME = 1; // Minimum reading time in minutes

  /**
   * Calculate reading time based on word count
   * @param {string} text - The text content to analyze
   * @returns {number} - Estimated reading time in minutes
   */
  function calculateReadingTime(text) {
    if (!text || typeof text !== 'string') return MIN_READ_TIME;

    // Remove extra whitespace and split into words
    const words = text.trim().split(/\s+/).filter(word => word.length > 0);
    const wordCount = words.length;

    // Calculate reading time (round up)
    const minutes = Math.ceil(wordCount / WORDS_PER_MINUTE);

    // Return at least minimum reading time
    return Math.max(minutes, MIN_READ_TIME);
  }

  /**
   * Format reading time for display
   * @param {number} minutes - Reading time in minutes
   * @returns {string} - Formatted reading time text
   */
  function formatReadingTime(minutes) {
    if (minutes === 1) {
      return ' 1 min read';
    }
    return ` ${minutes} min read`;
  }

  /**
   * Extract text content from post body
   * @param {string} postUrl - URL of the post
   * @returns {Promise<string>} - Post text content
   */
  async function fetchPostContent(postUrl) {
    try {
      const response = await fetch(postUrl);
      if (!response.ok) throw new Error('Failed to fetch post');

      const html = await response.text();
      const parser = new DOMParser();
      const doc = parser.parseFromString(html, 'text/html');

      // Find post body - try common Blogger selectors
      const postBody = doc.querySelector('.post-body, [itemprop="articleBody"], .entry-content, article .post-content');

      if (!postBody) {
        console.warn('Post body not found for:', postUrl);
        return '';
      }

      // Get text content, excluding scripts and styles
      const scripts = postBody.querySelectorAll('script, style');
      scripts.forEach(el => el.remove());

      return postBody.textContent || postBody.innerText || '';

    } catch (error) {
      console.error('Error fetching post content:', error);
      return '';
    }
  }

  /**
   * Update reading time display for a post card
   * @param {Element} readingTimeElement - The reading time display element
   * @param {string} postUrl - URL of the post
   */
  async function updateReadingTime(readingTimeElement, postUrl) {
    const textSpan = readingTimeElement.querySelector('.reading-time-text');
    if (!textSpan) return;

    try {
      // For post list pages, use snippet as approximation
      const postCard = readingTimeElement.closest('.post-card');
      let textContent = '';

      if (postCard) {
        // Try to get snippet text
        const snippetElement = postCard.querySelector('.post-snippet');
        if (snippetElement) {
          textContent = snippetElement.textContent || '';
          // Approximate full post length based on snippet (assuming snippet is ~10% of full post)
          const approximateWords = textContent.trim().split(/\s+/).length * 10;
          const minutes = Math.ceil(approximateWords / WORDS_PER_MINUTE);
          textSpan.textContent = formatReadingTime(Math.max(minutes, MIN_READ_TIME));
          return;
        }
      }

      // If on individual post page, fetch full content
      const content = await fetchPostContent(postUrl);
      const readingTime = calculateReadingTime(content);
      textSpan.textContent = formatReadingTime(readingTime);

    } catch (error) {
      console.error('Error updating reading time:', error);
      textSpan.textContent = ' 5 min read'; // Fallback
    }
  }

  /**
   * Initialize reading time calculations for all posts
   */
  function initReadingTime() {
    // Find all reading time elements
    const readingTimeElements = document.querySelectorAll('.post-reading-time');

    readingTimeElements.forEach(element => {
      const postId = element.getAttribute('data-post-id');

      // Get post URL from the post card
      const postCard = element.closest('.post-card');
      if (!postCard) return;

      const postLink = postCard.querySelector('.post-title a, .read-more-btn');
      if (!postLink) return;

      const postUrl = postLink.getAttribute('href');
      if (!postUrl) return;

      // Update reading time
      updateReadingTime(element, postUrl);
    });

    // Handle individual post pages (if post body is on the same page)
    const postBody = document.querySelector('.post-body, [itemprop="articleBody"], .entry-content');
    if (postBody) {
      const textContent = postBody.textContent || postBody.innerText || '';
      const readingTime = calculateReadingTime(textContent);

      // Update all reading time displays on the page with actual calculation
      readingTimeElements.forEach(element => {
        const textSpan = element.querySelector('.reading-time-text');
        if (textSpan) {
          textSpan.textContent = formatReadingTime(readingTime);
        }
      });
    }
  }

  // Initialize on DOM ready
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', initReadingTime);
  } else {
    initReadingTime();
  }

  // Re-initialize if content is dynamically loaded
  window.addEventListener('load', initReadingTime);

})();
