(function() {
  'use strict';

  /**
   * Generate a URL-friendly slug from text
   */
  function slugify(text) {
    return text
      .toString()
      .toLowerCase()
      .trim()
      .replace(/\s+/g, '-')           // Replace spaces with -
      .replace(/[^\w\-]+/g, '')       // Remove non-word chars
      .replace(/\-\-+/g, '-')         // Replace multiple - with single -
      .replace(/^-+/, '')              // Trim - from start
      .replace(/-+$/, '');             // Trim - from end
  }

  /**
   * Check if current page is a single post page
   */
  function isSinglePostPage() {
    // Check if body has item view class or if we can find a single post container
    const body = document.body;
    const hasItemView = body.classList.contains('item-view') || 
                       body.classList.contains('post-view') ||
                       body.querySelector('.post-body, [itemprop="articleBody"], .entry-content, article .content');

    // Also check URL pattern - Blogger single posts typically have .html extension
    const url = window.location.pathname;
    const isSinglePost = url.includes('.html') || body.classList.contains('item');

    return hasItemView && isSinglePost;
  }

  /**
   * Find the main post content container
   */
  function findPostContent() {
    const selectors = [
      '.post-body',
      '[itemprop="articleBody"]',
      '.entry-content',
      'article .content',
      '.post-content',
      'article',
      '.blog-post',
      'main article'
    ];

    for (let selector of selectors) {
      const content = document.querySelector(selector);
      if (content && content.querySelector('h2, h3')) {
        return content;
      }
    }

    return null;
  }

  /**
   * Extract headings from post content
   */
  function extractHeadings(container) {
    const headings = container.querySelectorAll('h2, h3');
    const headingData = [];
    const usedIds = new Set();

    headings.forEach((heading, index) => {
      const level = heading.tagName.toLowerCase();
      const text = heading.textContent.trim();

      if (!text) return; // Skip empty headings

      // Generate or use existing ID
      let id = heading.id;
      if (!id) {
        id = slugify(text);
        // Ensure unique IDs
        let uniqueId = id;
        let counter = 1;
        while (usedIds.has(uniqueId)) {
          uniqueId = `${id}-${counter}`;
          counter++;
        }
        id = uniqueId;
        heading.id = id;
      }
      usedIds.add(id);

      headingData.push({
        id: id,
        text: text,
        level: level,
        element: heading
      });
    });

    return headingData;
  }

  /**
   * Build nested TOC structure
   */
  function buildTOCStructure(headings) {
    const structure = [];
    let currentH2 = null;

    headings.forEach(heading => {
      if (heading.level === 'h2') {
        currentH2 = {
          ...heading,
          children: []
        };
        structure.push(currentH2);
      } else if (heading.level === 'h3' && currentH2) {
        currentH2.children.push(heading);
      }
    });

    return structure;
  }

  /**
   * Generate TOC HTML
   */
  function generateTOCHTML(structure) {
    if (!structure.length) {
      return '<p class="toc-empty">No headings found</p>';
    }

    let html = '<ul class="toc-list">';

    structure.forEach(h2 => {
      html += `
        <li class="toc-item">
          <a href="#${h2.id}" class="toc-link" data-target="${h2.id}">
            ${h2.text}
          </a>`;

      if (h2.children.length > 0) {
        html += '<ul class="toc-list">';
        h2.children.forEach(h3 => {
          html += `
            <li class="toc-item">
              <a href="#${h3.id}" class="toc-link" data-target="${h3.id}">
                ${h3.text}
              </a>
            </li>`;
        });
        html += '</ul>';
      }

      html += '</li>';
    });

    html += '</ul>';
    return html;
  }

  /**
   * Create TOC container element
   */
  function createTOCContainer() {
    const toc = document.createElement('aside');
    toc.className = 'toc-container';
    toc.innerHTML = `
      <div class="toc-header">
        <svg class="toc-icon" width="20" height="20" viewBox="0 0 24 24" fill="none">
          <path d="M3 6h18M3 12h18M3 18h18" stroke="currentColor" stroke-width="2" stroke-linecap="round"/>
        </svg>
        <h2 class="toc-title">Contents</h2>
      </div>
      <nav class="toc-nav"></nav>
    `;
    return toc;
  }

  /**
   * Smooth scroll to target
   */
  function smoothScrollTo(targetId) {
    const target = document.getElementById(targetId);
    if (target) {
      const offset = 100; // Account for fixed header
      const targetPosition = target.getBoundingClientRect().top + window.pageYOffset - offset;

      window.scrollTo({
        top: targetPosition,
        behavior: 'smooth'
      });
    }
  }

  /**
   * Highlight active section based on scroll position
   */
  function updateActiveSection() {
    const tocLinks = document.querySelectorAll('.toc-link');
    if (!tocLinks.length) return;

    const headings = Array.from(tocLinks).map(link => {
      const targetId = link.getAttribute('data-target');
      return document.getElementById(targetId);
    }).filter(Boolean);

    if (!headings.length) return;

    // Find the current active heading based on scroll position
    const scrollPosition = window.scrollY + 150; // Offset for header
    let activeHeading = null;

    for (let i = headings.length - 1; i >= 0; i--) {
      const heading = headings[i];
      if (heading.offsetTop <= scrollPosition) {
        activeHeading = heading;
        break;
      }
    }

    // Update active states
    tocLinks.forEach(link => {
      link.classList.remove('active');
      if (activeHeading && link.getAttribute('data-target') === activeHeading.id) {
        link.classList.add('active');
      }
    });
  }

  /**
   * Throttle function for scroll performance
   */
  function throttle(func, wait) {
    let timeout;
    return function() {
      if (!timeout) {
        timeout = setTimeout(() => {
          func();
          timeout = null;
        }, wait);
      }
    };
  }

  /**
   * Initialize Table of Contents
   */
  function initTableOfContents() {
    // Only run on single post pages
    if (!isSinglePostPage()) {
      return;
    }

    // Find post content
    const postContent = findPostContent();
    if (!postContent) {
      console.log('TOC: No post content found');
      return;
    }

    // Extract headings
    const headings = extractHeadings(postContent);
    if (!headings.length) {
      console.log('TOC: No headings found in post');
      return;
    }

    console.log(`TOC: Found ${headings.length} headings`);

    // Build structure
    const structure = buildTOCStructure(headings);

    // Create TOC container
    const tocContainer = createTOCContainer();
    const tocNav = tocContainer.querySelector('.toc-nav');
    tocNav.innerHTML = generateTOCHTML(structure);

    // Append to body
    document.body.appendChild(tocContainer);

    // Add click handlers for smooth scrolling
    const tocLinks = tocContainer.querySelectorAll('.toc-link');
    tocLinks.forEach(link => {
      link.addEventListener('click', (e) => {
        e.preventDefault();
        const targetId = link.getAttribute('data-target');
        smoothScrollTo(targetId);

        // Update URL hash
        if (history.pushState) {
          history.pushState(null, null, `#${targetId}`);
        } else {
          window.location.hash = targetId;
        }
      });
    });

    // Add scroll listener for active highlighting
    const throttledUpdate = throttle(updateActiveSection, 100);
    window.addEventListener('scroll', throttledUpdate);

    // Initial active section update
    updateActiveSection();

    // Handle initial hash in URL
    if (window.location.hash) {
      setTimeout(() => {
        const targetId = window.location.hash.substring(1);
        smoothScrollTo(targetId);
      }, 100);
    }
  }

  // Initialize when DOM is ready
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', initTableOfContents);
  } else {
    initTableOfContents();
  }

  // Also try on window load in case content loads late
  window.addEventListener('load', () => {
    if (!document.querySelector('.toc-container')) {
      initTableOfContents();
    }
  });

})();
