(function() {
  'use strict';

  /**
   * Initialize Load More functionality
   */
  function initLoadMore() {
    const loadMoreBtn = document.getElementById('load-more-posts');

    if (!loadMoreBtn) {
      console.log('Load More: Button not found (no more pages)');
      return;
    }

    const postsGrid = document.querySelector('.posts-grid');

    if (!postsGrid) {
      console.error('Load More: Posts grid container not found');
      return;
    }

    let isLoading = false;
    let currentPage = 1;

    // Get initial next URL from button data attribute
    let nextPageUrl = loadMoreBtn.getAttribute('data-next-url');

    /**
     * Fetch next page of posts via AJAX
     */
    async function loadMorePosts() {
      if (isLoading || !nextPageUrl) {
        return;
      }

      isLoading = true;

      // Update button to loading state
      loadMoreBtn.classList.add('loading');
      loadMoreBtn.disabled = true;

      try {
        console.log(`Load More: Fetching ${nextPageUrl}`);

        // Fetch the next page
        const response = await fetch(nextPageUrl);

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const html = await response.text();

        // Parse the HTML response
        const parser = new DOMParser();
        const doc = parser.parseFromString(html, 'text/html');

        // Extract new post cards
        const newPosts = doc.querySelectorAll('.post-card');

        if (newPosts.length === 0) {
          console.log('Load More: No posts found in response');
          showEndMessage();
          return;
        }

        console.log(`Load More: Found ${newPosts.length} new posts`);

        // Add fade-in class and append posts with staggered animation
        newPosts.forEach((post, index) => {
          post.classList.add('ajax-loaded');
          post.style.animationDelay = `${index * 0.1}s`;
          postsGrid.appendChild(post.cloneNode(true));
        });

        // Update next page URL
        const nextLoadMoreBtn = doc.getElementById('load-more-posts');
        if (nextLoadMoreBtn) {
          nextPageUrl = nextLoadMoreBtn.getAttribute('data-next-url');
          loadMoreBtn.setAttribute('data-next-url', nextPageUrl);
          console.log(`Load More: Next URL updated to ${nextPageUrl}`);
        } else {
          // No more pages
          nextPageUrl = null;
          console.log('Load More: No more pages available');
          showEndMessage();
          return;
        }

        // Update page counter
        currentPage++;

        // Re-initialize reading time for new posts
        if (typeof initReadingTime === 'function') {
          setTimeout(() => {
            initReadingTime();
          }, 100);
        }

        // Scroll to first new post smoothly
        setTimeout(() => {
          const firstNewPost = postsGrid.querySelector('.post-card.ajax-loaded');
          if (firstNewPost) {
            const offset = 120; // Header height
            const targetPosition = firstNewPost.getBoundingClientRect().top + window.pageYOffset - offset;

            window.scrollTo({
              top: targetPosition,
              behavior: 'smooth'
            });
          }
        }, 200);

      } catch (error) {
        console.error('Load More: Error loading posts', error);
        showErrorMessage(error.message);
      } finally {
        isLoading = false;
        loadMoreBtn.classList.remove('loading');
        loadMoreBtn.disabled = false;
      }
    }

    /**
     * Show end of posts message
     */
    function showEndMessage() {
      loadMoreBtn.disabled = true;
      loadMoreBtn.classList.add('loading');

      const btnText = loadMoreBtn.querySelector('.btn-text');
      if (btnText) {
        btnText.textContent = 'No More Posts';
      }

      const icon = loadMoreBtn.querySelector('.btn-icon');
      if (icon) {
        icon.innerHTML = '<circle cx="10" cy="10" r="1.5" fill="currentColor"/><circle cx="6" cy="10" r="1.5" fill="currentColor"/><circle cx="14" cy="10" r="1.5" fill="currentColor"/>';
      }

      // Hide button after delay
      setTimeout(() => {
        loadMoreBtn.style.transition = 'opacity 0.5s ease, transform 0.5s ease';
        loadMoreBtn.style.opacity = '0';
        loadMoreBtn.style.transform = 'scale(0.9)';

        setTimeout(() => {
          const container = loadMoreBtn.closest('.load-more-container');
          if (container) {
            container.innerHTML = '<div class="load-more-message"> You\'ve reached the end! That\'s all the posts.</div>';
          }
        }, 500);
      }, 1500);
    }

    /**
     * Show error message
     */
    function showErrorMessage(message) {
      const btnText = loadMoreBtn.querySelector('.btn-text');
      if (btnText) {
        btnText.textContent = 'Error Loading';
      }

      // Show error notification
      const container = loadMoreBtn.closest('.load-more-container');
      if (container) {
        const errorDiv = document.createElement('div');
        errorDiv.className = 'load-more-message';
        errorDiv.style.color = '#ef4444';
        errorDiv.textContent = ` Failed to load posts. ${message}`;
        container.appendChild(errorDiv);

        // Remove error after 5 seconds
        setTimeout(() => {
          errorDiv.remove();
          const btnText = loadMoreBtn.querySelector('.btn-text');
          if (btnText) {
            btnText.textContent = 'Try Again';
          }
        }, 5000);
      }
    }

    // Add click event listener
    loadMoreBtn.addEventListener('click', loadMorePosts);

    // Optional: Auto-load when scrolling near bottom (infinite scroll)
    // Uncomment to enable
    /*
    let autoLoadEnabled = true;

    function checkAutoLoad() {
      if (!autoLoadEnabled || isLoading || !nextPageUrl) return;

      const btnPosition = loadMoreBtn.getBoundingClientRect();
      const windowHeight = window.innerHeight;

      // Trigger when button is 300px from viewport
      if (btnPosition.top < windowHeight + 300) {
        autoLoadEnabled = false; // Prevent multiple triggers
        loadMorePosts().then(() => {
          autoLoadEnabled = true;
        });
      }
    }

    window.addEventListener('scroll', throttle(checkAutoLoad, 200));
    */

    console.log('Load More: Initialized successfully');
  }

  /**
   * Throttle function for performance
   */
  function throttle(func, wait) {
    let timeout;
    return function() {
      if (!timeout) {
        timeout = setTimeout(() => {
          func();
          timeout = null;
        }, wait);
      }
    };
  }

  // Initialize when DOM is ready
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', initLoadMore);
  } else {
    initLoadMore();
  }

  // Also try on window load
  window.addEventListener('load', () => {
    if (!document.getElementById('load-more-posts')?.dataset.initialized) {
      initLoadMore();
    }
  });

})();
